/*Table structure for table `table_article` */

DROP TABLE IF EXISTS `table_article`;

CREATE TABLE `table_article` (
  `id` int(11) NOT NULL AUTO_INCREMENT COMMENT 'id',
  `siteid` int(11) NOT NULL,
  `cateid` int(11) NOT NULL DEFAULT '0',
  `title` varchar(200) NOT NULL COMMENT '标题',
  `subtitle` varchar(200) DEFAULT NULL COMMENT '副标题',
  `source` varchar(50) DEFAULT NULL COMMENT '来源',
  `author` varchar(50) DEFAULT NULL COMMENT '作者',
  `description` varchar(500) DEFAULT NULL,
  `thumb` varchar(250) DEFAULT NULL COMMENT '缩略图',
  `images` text COMMENT 'json图片列表',
  `create_time` int(11) NOT NULL COMMENT '创建时间',
  `send_time` int(11) NOT NULL COMMENT '发布时间',
  `update_time` int(11) NOT NULL COMMENT '更新时间',
  `view_num` int(11) NOT NULL COMMENT '查看次数',
  `comment_num` int(11) NOT NULL DEFAULT '0' COMMENT '评论数',
  `like_num` int(11) NOT NULL DEFAULT '0' COMMENT '喜欢数',
  `sort` int(11) DEFAULT '100' COMMENT '排序',
  `rec_type` tinyint(1) NOT NULL COMMENT '推荐类型；1最新，2最热',
  `show_type` tinyint(1) NOT NULL DEFAULT '0' COMMENT '显示状态，0无图，1一张小图，2一张大图，3三张小图片',
  `status` tinyint(2) NOT NULL DEFAULT '0' COMMENT '状态 0正常',
  `keywords` varchar(50) DEFAULT NULL COMMENT '文章关键词',
  PRIMARY KEY (`id`),
  KEY `title` (`title`),
  KEY `create_time` (`create_time`),
  KEY `view_num` (`view_num`),
  KEY `like_num` (`like_num`),
  KEY `sort` (`sort`),
  KEY `status` (`status`),
  KEY `rec_type` (`rec_type`),
  KEY `send_time` (`send_time`),
  KEY `siteid` (`siteid`)
) ENGINE=InnoDB AUTO_INCREMENT=8 DEFAULT CHARSET=utf8;

